{-# OPTIONS_GHC -Wall -Werror #-}

module Day1( result1
           , result2
           , tests
           ) where

import Test.HUnit

result1 :: Int
result1 = foldr upDown 0 input

result2 :: Int
result2 = length . takeWhile (>=0) . scanl (flip upDown) 0 $ input

upDown :: Char -> Int -> Int
upDown '(' = (+1)
upDown ')' = subtract 1
upDown _   = undefined

tests :: IO Counts
tests = runTestTT $ 
    TestList [ upDown '(' 4          ~?= 5
             , upDown ')' (-1)       ~?= (-2)
             , result1               ~?= 138
             , result2               ~?= 1771
             ]

